(function() {
  var get_group_by_invite_code, invite_codes_element, redraw_table, set_error;

  invite_codes_element = "";

  $(function($) {
    invite_codes_element = $("input#invitation_code");
    if (_.isElement($("form#user_registration")[0])) {
      $("#add_group_button").click(function(e) {
        get_group_by_invite_code();
        return e.preventDefault();
      });
      $("#invitation_code").keypress(function(e) {
        if (e.keyCode === 13) {
          get_group_by_invite_code();
          return e.preventDefault();
        }
      });
      return redraw_table();
    }
  });

  get_group_by_invite_code = function() {
    var invitation_code;
    invitation_code = $(invite_codes_element).val().toLowerCase();
    if (invitation_code === "") {
      return set_error("No invitation code entered");
    }
    return $.getJSON("/groups/by_code/" + invitation_code).success(function(group, textStatus, jqXHR) {
      var ref, the_codes;
      if (group && group.status === 'success') {
        the_groups[group.invite_code] = group;
        the_codes = (ref = $(invite_codes_element).data("codes")) != null ? ref : {};
        $(invite_codes_element).data("codes", _.extend(the_codes, _.zip([group.invite_code], [group.invite_code])));
        $(invite_codes_element).val("");
        return redraw_table();
      } else {
        if (group.status === 'invalid') {
          return set_error("Invalid invite code");
        } else {
          return set_error("This group has reached its maximum group size. Please contact your Group Admin for help.");
        }
      }
    }).error(function(jqXHR, textStatus, thrownError) {
      return set_error("An error occured. Please check your invite code.");
    });
  };

  set_error = function(error) {
    var alert;
    $("#groups .alert").remove();
    alert = $("<div class=\"alert alert-error top-space-sm\">" + error + "<a href=\"#\" class=\"close\" data-dismiss=\"alert\">&times;</a></div>");
    $("#groups").append(alert);
    setTimeout((function() {
      return $(alert).remove();
    }), 4000);
    return false;
  };

  redraw_table = function() {
    var inputs, out, the_codes, the_groups;
    the_groups = window.the_groups || false;
    if (!the_groups) {
      return;
    }
    the_codes = $("input#invitation_code").data("codes");
    out = "";
    inputs = "";
    _.each(the_groups, function(group) {
      if (group != null) {
        out += "<tr><td>" + group.name;
        if (group.admin) {
          out += " <a class='admin-code-icon is-tooltip text-info' data-toggle='tooltip' href='#' title='You have entered a group admin code. When you complete registration a new group will be created and you will be added as a group admin.'><i class='icon-key'></i></a>";
        }
        out += "</td><td><a href=\"#\" class=\"delete-invite-code\" data-group-name=\"" + group.name + "\" data-invite-code=\"" + group.invite_code + "\"><i class=\"fa fa-trash-o\"></i></a></td></tr>";
        return inputs += "<input type=\"hidden\" name=\"invite_codes[]\" value=\"" + group.invite_code + "\" />";
      }
    });
    $("#groups-table").html(out);
    $("#groups-hidden-inputs").html(inputs);
    return $(".admin-code-icon").tooltip();
  };

  $(document).on("click", "a.delete-invite-code", function(event) {
    var invite_code, ref, the_codes;
    event.preventDefault();
    invite_code = $(this).data("invite-code");
    the_codes = (ref = $(invite_codes_element).data("codes")) != null ? ref : {};
    delete the_groups[invite_code];
    delete the_codes[invite_code];
    $(invite_codes_element).data("codes", _.omit(the_codes, invite_code));
    return redraw_table();
  });

}).call(this);
