$(function($) {
  var s,
  AssignmentBox = {
    settings: {
      hideCheckboxes: true,
      box: "[data-assignment-box=wrapper]",
      availableBox: "[data-assignment-box=available]",
      assignedBox: "[data-assignment-box=assigned]",
      assignBtn: "[data-assignment-box=assign-actions]",
      buildAssignBtn: function() {
        return $('<button type="button" class="btn btn-primary btn-xs pull-right"><i class="fa fa-arrow-right"></i></button>');
      },
      buildUnassignBtn: function() {
        return $('<button type="button" class="btn btn-primary btn-xs pull-left"><i class="fa fa-arrow-left"></i></button>');
      },
      countTemplate: function(count) {
        return count
      }
    },

    init: function() {
      s = this.settings;
      this.bindAssign();
      this.bindUnassign();
      this.bindAddAll();
      this.bindRemoveAll();
      this.bindSearch();

      if (s.hideCheckboxes) {
        $(s.box).find("input[type=checkbox]").hide();
      }
    },

    bindAssign: function() {
      $(s.availableBox).on('click', s.assignBtn + ' button', function(e) {
        AssignmentBox.assign(this, true, s.assignedBox, s.buildUnassignBtn, 'assign');
      });
    },

    bindUnassign: function() {
      $(s.assignedBox).on('click', s.assignBtn + ' button', function(e) {
        AssignmentBox.assign(this, false, s.availableBox, s.buildAssignBtn, 'unassign');
      });
    },

    assign: function(ele, checkboxStatus, newBox, newButton, swapAction) {
      var scope = $(ele).closest(s.box);
      var row   = $(ele).closest("tr");

      AssignmentBox.check(row, checkboxStatus);
      AssignmentBox.move(scope, row, newBox);
      AssignmentBox.swapButtons(row, newButton(), swapAction)
      AssignmentBox.updateCounts(scope);
    },

    bindAddAll: function() {
      $(s.box).on('click', '[data-assignment-box-bulk-move=available]', function(e) {
        AssignmentBox.bulkMove(this, true, s.availableBox, s.assignedBox, s.buildUnassignBtn, 'assign');
      });
    },

    bindRemoveAll: function() {
      $(s.box).on('click', '[data-assignment-box-bulk-move=assigned]', function(e) {
        AssignmentBox.bulkMove(this, false, s.assignedBox, s.availableBox, s.buildAssignBtn, 'unassign');
      });
    },

    bulkMove: function(ele, checkboxStatus, currentBox, targetBox, newButton, swapAction) {
      var scope = $(ele).closest(s.box);
      // Filter on visible so elements hidden by search aren't acted on.
      var rows  = scope.find(currentBox).find("tr").filter(":visible");
      AssignmentBox.check(rows, checkboxStatus);
      AssignmentBox.move(scope, rows, targetBox);

      $.each(rows, function(index, row) {
        AssignmentBox.swapButtons(row, newButton(), swapAction);
      });

      AssignmentBox.updateCounts(scope);
    },

    bindSearch: function() {
      $("input[type=search][data-assignment-box-target]").keyup(function() {
        // Hack to make jQuery's contains case insensitive
        $.expr[":"].contains = $.expr.createPseudo(function(arg) {
          return function( elem ) {
            return $(elem).text().toUpperCase().indexOf(arg.toUpperCase()) >= 0;
          };
        });

        var scope               = $(this).closest(s.box);
        var assignmentBoxTarget = $(this).data("assignment-box-target");
        var assignmentBox       = $(scope).find("[data-assignment-box=" + assignmentBoxTarget + "] table");

        var matches    = assignmentBox.find("tr").filter(":contains('" + $(this).val() + "')");
        var nonMatches = assignmentBox.find("tr").not(":contains('" + $(this).val() + "')");

        $(matches).show();
        $(nonMatches).hide();
      });
    },

    check: function(scope, status) {
      $(scope).find("input[type=checkbox]").prop("checked", status);
    },

    move: function(scope, ele, newBox) {
      $(scope)
        .find(newBox)
        .find("tbody")
        .prepend($(ele));
    },

    swapButtons: function(row, button, swapAction) {
      var actions = $(row).find(s.assignBtn);
      actions.remove();
      actions.find("button").replaceWith(button);
      if (swapAction == "assign") {
        $(row).prepend(row, actions);
      } else {
        $(row).append(row, actions);
      }
    },

    updateCounts: function(scope) {
      var $availableBox = $(scope).find(s.availableBox),
        $assignedBox = $(scope).find(s.assignedBox),
        availableCount = s.countTemplate($availableBox.find("tr").size()),
        assignedCount = s.countTemplate($assignedBox.find("tr").size());

      $availableBox.find("[data-assignment-box=available-count]").text(availableCount);
      $assignedBox.find("[data-assignment-box=assigned-count]").text(assignedCount);
    }
  };

  AssignmentBox.init();
});
