$(document).ready(function($){
  $.ajaxSetup({ cache: false});
  function updateUnreadMessageCount(ele) {
    $(ele).closest('tr').addClass("active");
    $.getJSON('/total_unread_communications/dashboard', function(data) {
      $("[data-messages-link=true]").replaceWith(data.link);
    });
  };

  $("[data-target='#messageModal']").on('click', function(){
		$(this).parent('.message').removeClass('unread');
    var element = this;

    $.get($(this).attr("href")+".json", function(data) {
      $("#messageModal .title").text(data.subject);
      $("#messageModal .from").html("<strong>FROM:</strong> " + data.sender_name);
      $("#messageModal .sent_on").html("<strong>SENT ON:</strong> " + data.delivery_date);
      if (data.attachment_filename) {
        $("#messageModal .attachment_filename").html("</br><strong>ATTACHMENT:</strong> " + "<a href=\"" + data.attachment_url +"\">"+data.attachment_filename+"</a>");
        $("#messageModal .attachment_filename").show();
      }
      else {
        $("#messageModal .attachment_filename").hide();
      }
      $("#messageModal .content").html(data.message);
      $('#messageModal').modal('show');
    })
      .success(function() {
        updateUnreadMessageCount(element);
      });

    return false;
  });
});
