        // initialDate = dateKeys[0];
        // finalDate = dateKeys[dateKeys.length - 1];
        // initialValue = data.values_by_date[initialDate][0].value

        $.each(data.values_by_date, function() {
          $.each(this, function() {
            var entry = { "date": this["entry_date"] }
              , value = this["value"];

            // if (value !== null && initialValue === null) {
            //   initialValue = value;
            // }
            //
            // if (value !== null) {
            //   finalValue = value;
            // }

            // Verified entries get displayed differently.
            if (this["verified"] === true && value !== null) {
              entry["column-2"] = value.toPrecision(3)
            } else if (value !== null) {
              entry["column-1"] = value.toPrecision(3);
            }

            dataProvider.push(entry);
          });
        });
        //
