$(function() {
  var ProfileEditor = {
    init: function() {
      this.cache();
      this.bind();
    },

    cache: function() {
      this.$nickname = $("[data-nickname]");
      this.$nicknameBtns = $("[data-nickname-btn]");
      this.$nicknameForm = $("#nickname_form");
      this.$nicknameField = $("#user_nickname");
      this.$nicknameEditBtn = $("[data-nickname-btn=edit]");
      this.$nicknameAddBtn = $("[data-nickname-btn=add]");
      this.$locationToggle = $("#display_location_toggle");
    },

    bind: function() {
      this.handleNicknameBtnClick();
      this.handleNicknameUpdate();
      this.handleDisplayLocationUpdate();
    },

    handleNicknameBtnClick: function() {
      var _this = this;

      _this.$nicknameBtns.click(function(e) {
        e.preventDefault();

        _this.$nicknameBtns.hide();
        _this.$nickname.hide();
        _this.$nicknameForm.hide().removeClass("hidden").fadeIn();
        _this.$nicknameField.select();
      });
    },

    nicknameSuccessCallback: function(data, textStatus, settings) {
      this.profileEditor.$nicknameForm.hide();
      this.profileEditor.$nickname.text(data.nickname);
      this.profileEditor.$nickname.fadeIn();

      if (data.nickname.length > 0) {
        this.profileEditor.$nicknameEditBtn.fadeIn();
      } else {
        this.profileEditor.$nicknameAddBtn.fadeIn();
      }
    },

    nicknameFailureCallback: function(data) {
      var errors = $.parseJSON(data.responseText).errors
        , alertBox = window.createJsonErrorsAlert(errors, "list-unstyled");

      this.profileEditor.$nicknameForm.prepend(alertBox);
    },

    clearNicknameErrors: function() {
      this.$nicknameForm.find(".alert-error").remove();
    },

    displayLocationSuccessCallback: function(data) {
      this.profileEditor.$locationToggle.data("set-profile-display-location-to", !data.display_location);
      if (data.display_location) {
        this.profileEditor.$locationToggle.text("(hide from my profile)").hide().fadeIn("slow");
      } else {
        this.profileEditor.$locationToggle.text("(show on my profile)").hide().fadeIn("slow");
      }
    },

    updateProfileConfig: function(url, data, profileEditor) {
      return {
        method: "PUT",
        url: url,
        data: data,
        dataType: "json",
        profileEditor: profileEditor
      }
    },

    handleNicknameUpdate: function() {
      var _this = this;

      _this.$nicknameForm.submit(function(e) {
        e.preventDefault();

        _this.clearNicknameErrors();

        var formData = $(this).serialize()
          , url = $(this).prop("action");

        $.ajax(_this.updateProfileConfig(url, formData, _this))
          .success(_this.nicknameSuccessCallback)
          .fail(_this.nicknameFailureCallback);
      });
    },

    handleDisplayLocationUpdate: function() {
      var _this = this;

      this.$locationToggle.on("click", function(e) {
        e.preventDefault();

        var url = $(this).prop("href")
          , display_location = $(this).data("set-profile-display-location-to");

        $.ajax(_this.updateProfileConfig(url, { user: { display_location: display_location } }, _this))
          .success(_this.displayLocationSuccessCallback);
      });
    }
  }

  ProfileEditor.init();
});
