$(function() {
  var SetDates = {
    init: function() {
      this.cache();
      this.bind();
    },

    cache: function() {
      this.$startDatePicker = $(".start-date-picker");
      this.$endDatePicker = $(".end-date-picker");
    },

    bind: function() {
      this.setEndDate();
      this.updateEndDate();
    },

    setEndDate: function() {
      var _this = this;

      var minDate = _this.$startDatePicker.first().datepicker('getDate');
      _this.$endDatePicker.datepicker({
        format: 'yyyy-mm-dd',
        autoclose: true
      }).datepicker('setStartDate', minDate);
    },

    updateEndDate: function() {
      var _this = this;

       // to handle when mobile datepicker is also present so that all end-date-pickers are updated rather than just the one changed
      if ( _this.$endDatePicker.size() > 1 ) {
        _this.$endDatePicker.datepicker({format: 'yyyy-mm-dd', autoclose: true}).on("changeDate", function(e) {
          _this.$endDatePicker.datepicker({
            format: 'yyyy-mm-dd',
            autoclose: true
          }).datepicker('update', $(this).datepicker('getDate'));
        });
      }

      _this.$startDatePicker.datepicker({format: 'yyyy-mm-dd', autoclose: true}).on("changeDate", function(e) {
        var startDate = $(this).datepicker('getDate');
        var endDate = _this.$endDatePicker.first().datepicker('getDate');
        if ( startDate > endDate ) {
          _this.$endDatePicker.datepicker({
            format: 'yyyy-mm-dd',
            autoclose: true
          }).datepicker('update', startDate);
        }

        _this.$endDatePicker.datepicker({
          format: 'yyyy-mm-dd',
          autoclose: true
        }).datepicker('setStartDate', startDate);
      });
    }
  }

  SetDates.init();
});
$(function() {
  window.hraCharts = []
  var c;
  var HraPieChartsManager = {
    config: {
      $hraPieReports: $(".hra-pie-report"),
      baseGreen: "#4CAE4C",
      baseRed: "#D0011B",
      positiveColors: [],
      negativeColors: []
    },

    init: function() {
      c = this.config;

      this.generateColors();
      this.loadCharts();
    },

    lightenDarkenColor: function(col, amt) {
      var usePound = false;

      if (col[0] == "#") {
        col = col.slice(1);
        usePound = true;
      }

      var num = parseInt(col,16);
      var r = (num >> 16) + amt;

      if (r > 255) r = 255;
      else if  (r < 0) r = 0;

      var b = ((num >> 8) & 0x00FF) + amt;

      if (b > 255) b = 255;
      else if  (b < 0) b = 0;

      var g = (num & 0x0000FF) + amt;

      if (g > 255) g = 255;
      else if (g < 0) g = 0;

      return (usePound?"#":"") + (g | (b << 8) | (r << 16)).toString(16);
    },

    generateColors: function() {
      for (i = 0; i < 50; i+=10) {
        c.positiveColors.push(this.lightenDarkenColor(c.baseGreen, i));
        c.negativeColors.push(this.lightenDarkenColor(c.baseRed, i));
      }
    },

    getColor: function(rating, index) {
      if (rating < 0) {
        return c.negativeColors[index];
      } else {
        return c.positiveColors[index];
      }
    },

    loadCharts: function() {
      var _self = this;

      c.$hraPieReports.each(function(index) {
        var answers = $(this).data("answers");
        var legendId = $(this).data("legend-id");
        var chartData = $.map(answers, function(answer, i) {
          return { "answer": answer.text, "total_responses": answer.value, "color": _self.getColor(answer.rating, i) }
        });
        var colors = $(this).data("colors");
        var chart = AmCharts.makeChart(this, HraPieChartsManager.chartConfig(chartData));

        window.hraCharts.push(chart);
      });
    },

    chartConfig: function(chartData) {
      return {
        "type": "pie",
        "labelsEnabled": false,
        "dataProvider": chartData,
        "valueField": "total_responses",
        "titleField": "answer",
        "colorField": "color",
        "balloon":{
          "fixedPosition": false
        },
        "export": {
          "enabled": true
        }
      }
    }
  }

  if ($(".hra-pie-report").length > 0) {
    HraPieChartsManager.init();

    $('.modal-with-charts').on('shown.bs.modal', function (e) {
      $.each(window.hraCharts, function(i, chart) {
        chart.invalidateSize()
      });
    });
  }
});
