$(function() {
  var incompleteActivity = $('#incomplete-activity');
  var countdownInProgress = $('#countdown-in-progress');
  var ticker = $('#ticker');
  var timerDescription = $('#timer-description');
  var description = $('#description');
  var returnLink = $('#return-link');

  if (incompleteActivity.length > 0) {
    var counter = 60;

    var interval = setInterval(function() {
      counter--;

      if (counter == 0) {
        clearInterval(interval);
        $.ajax({
          type: "GET",
          url: $('#timer-description').data('complete-activity-url'),
          data: {
            _method: 'GET'
          },
          dataType: 'json'
        })
        .success(function(data, textStatus, settings) {
          countdownInProgress.css('background-color', '#4CAE4C');
          countdownInProgress.css('border-color', '#2D682D');
          ticker.fadeOut(500, function() {
            $(this).html('<i class="fa fa-check"></i>');
            $(this).fadeIn(500);
          });
          timerDescription.css('background-color', '#4CAE4C');
          timerDescription.css('border-color', '#2D682D');
          timerDescription.css('color', '#ffffff');
          timerDescription.css('font-weight', 'bold');
          timerDescription.css('box-shadow', '0 15px 22px 0 rgba(50,50,93,.1), 0 11px 14px 0 rgba(0,0,0,.07)');
          description.html('Activity Complete!');
          returnLink.slideDown(500);
        })
        .fail(function(data) {
          alert("There was an error completing the activity, please refresh the page");
        });
      } else {
        ticker.html(counter);
      }
    }, 1000);
  }
});
$(function() {
  var PersonalPathWalkthrough = {
    init: function() {
      this.cache();
      this.bind();
    },

    cache: function() {
      this.$welcomeMessage = $('#welcome');
      this.$beginJouneyBtn = $('#begin-journey-btn');
      this.$continueJourneyBtn = $('#continue-journey-btn');
      this.$journeyDescription = $('#journey-description');
      this.$rewardsDescription = $('#rewards-description');
      this.$moduleDescriptions = $('#module-descriptions');
      this.$engageModulesList = $('#engage-modules-list');
      this.$moduleNameIntro = $('.module-name-intro');
      this.$moduleLayout = $('#module-layout');
      this.$modulePoints = $('.module-points');
      this.$yourPersonalPathBtn = $('#your-personal-path-btn');
      this.$moduleIntroDiv = $('#module-intro-div');
      this.$personalPathOverviewDiv = $('#personal-path-overview-div');
      this.$viewPersonalPathsBtn = $('#view-personal-paths-btn');
      this.$selectPersonalPathDiv = $('#select-personal-path-div');
      this.$slideRight = $('#slide-right');
      this.$slideLeft = $('#slide-left');
      this.$moveFullOverview = $('#move-full-overview');
      this.$healthFullOverview = $('#health-full-overview');
      this.$mindFullOverview = $('#mind-full-overview');
      this.$financeFullOverview = $('#finance-full-overview');
      this.$readyToSelectBtn = $('#ready-to-select-btn');
      this.$personalPathBtnSelections = $('#personal-path-btn-selections');
      this.$selectModule = $('.select-module');
      this.$selectPersonalPathDiv = $('#select-personal-path-div');
      this.$congratulationsDiv = $('#congratulations-div');
      this.$personalPath;
      this.$beginActivitiesBtn = $('#begin-activities-btn');
    },

    bind: function() {
      this.handleDisplayWelcomeMessage();
      this.handleBeginJourneyBtnClick();
      this.handleContinueJourneyBtnClick();
      this.handleYourPersonalPathBtnClick();
      this.handleViewPersonalPathsBtnClick();
      this.handleSlideRightBtnClick();
      this.handleSlideLeftBtnClick();
      this.handleReadyToSelectBtnClick();
      this.handleSelectModule();
      this.handleBeginActivitiesBtnClick();
    },

    handleDisplayWelcomeMessage: function() {
      var _this = this;

      _this.$welcomeMessage.fadeIn(800, function() {
        _this.$beginJouneyBtn.fadeIn(1500);
      });
    },

    handleBeginJourneyBtnClick: function() {
      var _this = this;

      _this.$beginJouneyBtn.on('click', function() {
        _this.$welcomeMessage.fadeOut(350, function() {
          _this.$journeyDescription.fadeIn(600, function() {
            _this.$engageModulesList.show();
            _this.$moduleNameIntro.each(function(fadeInDiv) {
              $(this).delay(fadeInDiv * 350).fadeIn(350);
            });
            _this.$continueJourneyBtn.delay(1500).fadeIn(500);
          });
        });
      });
    },

    handleContinueJourneyBtnClick: function() {
      var _this = this;

      _this.$continueJourneyBtn.on('click', function() {
        _this.$continueJourneyBtn.fadeOut(350);
        _this.$journeyDescription.fadeOut(350, function() {
          _this.$rewardsDescription.fadeIn(600, function() {
            _this.$moduleLayout.slideDown(700, function() {
              _this.$modulePoints.fadeIn(500, function() {
                _this.$yourPersonalPathBtn.fadeIn(600);
              });
            });
          });
        });
      });
    },

    handleYourPersonalPathBtnClick: function() {
      var _this = this;

      _this.$yourPersonalPathBtn.on('click', function() {
        _this.$moduleIntroDiv.fadeOut(350, function() {
          _this.$personalPathOverviewDiv.fadeIn(500);
        });
      });
    },

    handleViewPersonalPathsBtnClick: function() {
      var _this = this;

      _this.$viewPersonalPathsBtn.on('click', function() {
        _this.$personalPathOverviewDiv.fadeOut(350, function() {
          _this.$selectPersonalPathDiv.fadeIn(500);
        });
      });
    },

    handleSlideRightBtnClick: function() {
      var _this = this;

      _this.$slideRight.on('click', function() {
        if (_this.$moveFullOverview.is(':visible')) {
          _this.$moveFullOverview.fadeOut(200, function() {
            _this.$healthFullOverview.fadeIn(200);
          });
        } else if (_this.$healthFullOverview.is(':visible')) {
          _this.$healthFullOverview.fadeOut(200, function() {
            _this.$mindFullOverview.fadeIn(200);
          });
        } else if (_this.$mindFullOverview.is(':visible')) {
          _this.$mindFullOverview.fadeOut(200, function() {
            _this.$financeFullOverview.fadeIn(200);
          });
        } else {
          _this.$financeFullOverview.fadeOut(200, function() {
            _this.$moveFullOverview.fadeIn(200);
          });
        }
      });
    },

    handleSlideLeftBtnClick: function() {
      var _this = this;

      _this.$slideLeft.on('click', function() {
        if (_this.$moveFullOverview.is(':visible')) {
          _this.$moveFullOverview.fadeOut(200, function() {
            _this.$financeFullOverview.fadeIn(200);
          });
        } else if (_this.$healthFullOverview.is(':visible')) {
          _this.$healthFullOverview.fadeOut(200, function() {
            _this.$moveFullOverview.fadeIn(200);
          });
        } else if (_this.$mindFullOverview.is(':visible')) {
          _this.$mindFullOverview.fadeOut(200, function() {
            _this.$healthFullOverview.fadeIn(200);
          });
        } else {
          _this.$financeFullOverview.fadeOut(200, function() {
            _this.$mindFullOverview.fadeIn(200);
          });
        }
      });
    },

    handleReadyToSelectBtnClick: function() {
      var _this = this;

      _this.$readyToSelectBtn.on('click', function() {
        $(this).fadeOut(200, function() {
          _this.$personalPathBtnSelections.fadeIn(200);
        });
      });
    },

    handleSelectModule: function() {
      var _this = this;

      _this.$selectModule.on('click', function() {
        $(this).find('.btn:focus').css('outline', 'none');
        $(this).find('.btn').find('span').animate({opacity: 0}, function() {
          $(this).text("Confirm").animate({opacity: 1}, 200);
        });
        $(this).find('.btn').animate({width: '85px', backgroundColor: $(this).data('module-color'), borderColor: '#fff', color: '#fff'});
        $(this).on('click', function() {
          $(this).css('pointer-events', 'none');
          _this.$personalPath = $(this).data('module-name');
          $.ajax({
            type: "POST",
            url: $('[data-select-path-url]').data('select-path-url'),
            data: {
              _method: 'PATCH',
              user: {
                personal_path: $(this).data('module-name')
              }
            },
            dataType: 'json'
          })
          .success(function(data, textStatus, settings) {
            _this.$selectPersonalPathDiv.fadeOut(300, function() {
              _this.$congratulationsDiv.fadeIn(300);
            });
          })
          .fail(function(data) {
            alert("There was an error saving your personal path, please refresh the page");
          });
        });
      });
    },

    handleBeginActivitiesBtnClick: function() {
      var _this = this;

      _this.$beginActivitiesBtn.on('click', function() {
        window.location.replace($('[data-select-path-url]').data('select-path-url') + '/' + _this.$personalPath);
      });
    }
  }

  PersonalPathWalkthrough.init();
});
