function poll_report_status(report_url, run_count) {
  run_count = run_count + 1;
  setTimeout(function(){
    $.ajax({
      url: report_url,
      dataType: 'json',
      success: function(data) {
        if (data.fulfilled_at != null) {
          $("#report-"+data.id+" .report-link").html("<a href='"+data.primary_file.data.url+"'><i class='fa fa-download'></i></a>");
          $(".alert").slideUp(200, function() {
            $(this).html('<button type="button" class="close" data-dismiss="alert"><span aria-hidden="true">&times;</span><span class="sr-only">Close</span></button><strong>Completed: </strong>Your reports are ready! Download them from the list below.');
            $(this).removeClass("alert-info");
            $(this).addClass("alert-success");
          });
          $(".alert").delay(250).slideDown(200);
        } else if(run_count < 10) {
          poll_report_status(report_url, run_count);
        }
      }
    })
  }, 5000);
}

